	function rincitbl(a, b) {
		$.ajax({
				method: &quot;POST&quot;,
				url: &quot;http://localhost:8080/form_bantu/rinci/&quot; + a + &quot;/&quot; + b
			})
			.done(function(msg) {
				if (msg != &quot;&quot;) {
					$(&quot;#fbody&quot;).html(msg);
					$(&quot;#mBantu&quot;).modal({
						keyboard: false,
						backdrop: &#039;static&#039;
					});
				} else {
